let currentSlide=0,slides,dots;function showSlide(e){slides.forEach((t,n)=>{t.classList.toggle("active",n===e),dots[n].classList.toggle("active",n===e)})}function nextSlide(){currentSlide=(currentSlide+1)%slides.length,showSlide(currentSlide)}function prevSlide(){currentSlide=(currentSlide-1+slides.length)%slides.length,showSlide(currentSlide)}function goToSlide(e){currentSlide=e,showSlide(currentSlide)}document.addEventListener("DOMContentLoaded",()=>{slides=document.querySelectorAll(".slide"),dots=document.querySelectorAll(".dot"),showSlide(currentSlide),setInterval(nextSlide,1e4)})